				"FILE_FAILED",
				"FILE_ACCESS_DENIED",
				"FILE_NO_SPACE",
				"FILE_NAME_TOO_LONG",
				"FILE_TOO_LARGE",
				"FILE_VIRUS_INFECTED",
				"FILE_TRANSIENT_ERROR",
